var ArgumentParser=require('argparse').ArgumentParser,
	path=require('path');

var parser=new ArgumentParser({
	version: '0.2.0',
	addHelp: true,
	description: 'JavaDoc generation for JavaScript',
	prog: 'js-doc'
});

parser.addArgument(['-o','--out'],{metavar:'file',defaultValue:'readme',help:'set output file'});
parser.addArgument(['-p','--pkg'],{metavar:'path',defaultValue:'./',help:'path to package.json'});
parser.addArgument(['--files'],{nargs:'*',required:true,defaultValue:'./*.js',metavar:'f',help:'files to document'});
parser.addArgument(['--html'],{action:'storeTrue',help:'generate html instead of markdown'});
parser.addArgument(['-c','--css'],{defaultValue: path.join(__dirname,'..','src','github.css'),metavar:'file',help:'path to css file (default: github css)'});

module.exports=parser.parseArgs();